(()=>{const e=document.getElementById("movie-container");$.get("https://students-api.2.us-1.fl0.io/movies",(function(t){const n=function(e){return e.map((e=>{const t=document.createElement("div");return t.classList.add("movie-card"),t.innerHTML=`\n        <img src="${e.poster}" alt="${e.title}" class="estiloimg">\n        <h2>${e.title}</h2>\n        <p>Year: ${e.year}</p>\n        <p>Director: ${e.director}</p>\n        <p>Duration: ${e.duration}</p>\n        <p>Genre: ${e.genre.join(", ")}</p>\n        <p>Rate: ${e.rate}</p>`,t}))}(t);n.forEach((t=>e.appendChild(t)))}))})();